import { useState } from "react";
import "./App.css";

const [emotion, setEmotion] = useState("happy");

function App() {
  return (
    <div className="App">
      <h1>Current I am {emotion}</h1>
      <button onClick={setEmotion("Sad")}>Sad</button>
      <button onClick={setEmotion("Happy")}>Happy</button>
    </div>
  );
}

export default App;
